package the_5;

public class Test02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=50, num2=100;
		int big, diff;
		
		if(num1 > num2) {
			big = num1;
			System.out.println(big);
		}else {
			big = num2;
			System.out.println(big);
		}
		
		if(num1 > num2) {
			diff = num1-num2;
			System.out.println(diff);
		}else {
			diff = num2-num1;
			System.out.println(diff);
		}
		
		
	}
}
